import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';



const socket = io('localhost:3000');
function Index() {
    const [test, setTest] = useState('...');
    socket.on('now', data => {
        if(data) {
            console.log(data)
            setTest(data);
        }
        
    });

    socket.on('mensagem', data => {
        console.log(data)
        setTest(data);
    });

    const sendMessege = (e) => {
        socket.emit('mensagem', 'Nova Mensagem')
    }

    return(
        <div>
