import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';

function Index() {
    const [test, settest] = useState('...');

    useEffect(()=> {
        io().on('now', data => {
            if(data) {
                settest(data);
            }
        });
    }, []);

    const sendMessege = (e) => {
        console.log('')
        e.preventDefault();
        io().emit('now', 'Nova Mensagem')
    }

    return(
        <div>
            <h1>OlÃ¡ Next > {test}</h1>
            <input type='text'/>
            <button onClick= {() => sendMessege}>enviar</button>
        </div>
    );
} 

export default Index;
