import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';



const socket = io('localhost:3000');
socket.on('mensagem', data => {
    setTest(data);
})
function Index() {
    const [test, setTest] = useState('...');
    socket.on('now', data => {
        if(data) {
            socket.emit('mensagem', data)
            console.log(data)
            setTest(data);
        }
        
    });
    socket.on('mensagem', data => {
        setTest(data);
    })

    const sendMessege = (e) => {
        socket.emit('mensagem', 'Nova Mensagem')
    }

    return(
        <div>
            <h1>OlÃ¡ Next > {test}</h1>
            <input type='text'/>
            <button onClick= {() => sendMessege()}>enviar</button>
        </div>
    );
} 

export default Index;
