import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';




function Index() {
    const [test, setTest] = useState('...');
    useEffect(()=> {
        const socket = io('localhost:3000');
        socket.on('now', data => {
            if(data) {
                console.log(data)
                setTest(data);
            }
            
        });

        socket.on('mensagem', data => {
            setTest(data);
            console.log('botao apertado')
            socket.emit('mensagem', data);
        })
    }, []);

    socket.on('mensagem', data => {
        setTest(data);
        console.log(data)
    })

    const sendMessege = (e) => {
        socket.emit('mensagem', 'Nova Mensagem')
    }

    return(
        <div>
            <h1>OlÃ¡ Next > {test}</h1>
            <input type='text'/>
            <button onClick= {() => sendMessege()}>enviar</button>
        </div>
    );
} 

export default Index;
