import React, {useState, useEffect} from 'react';
import io from 'socket.io-client';

function Index() {
    const [test, settest] = useState('...');

    useEffect(()=> {
        io().on('now', data => {
            if(data) {
                console.log(data);
                settest(data);
            }
        });
    }, []);

    const sendMessege = () => {
        io().emit('')
    }

    return(
        <div>
            <h1>OlÃ¡ Next > {test}</h1>
            <input type='text'/>
            <button onClick= {()=> sendMessege}>enviar</button>
        </div>
    );
} 

export default Index;
